mapper.select:
select employee_id, name, reset_vacation_date, total_vacation_count, is_admin
from employee
where login_id = #{loginId} and password = #{password}

select ifnull(sum(vc.value), 0) as use_vacation_count
from vacation v join vacation_code vc on v.code = vc.code
where v.employee_id = #{employeeId} and v.use_status = 'U';
