mapper.update:
UPDATE vacation
SET code = #{code}, start = #{start}, end = #{end}, content = #{content}
WHERE idx = #{idx}

UPDATE vacation
SET use_status = 'U'
WHERE DATEDIFF(start, DATE_FORMAT(now(), '%Y-%m-%d')) >= 0;

UPDATE whole_vacation
SET use_status = 'U'
WHERE DATEDIFF(start, DATE_FORMAT(now(), '%Y-%m-%d')) >= 0;

UPDATE vacation
SET approval_status = 'C', cancellation_content = #{cancellationContent}
WHERE idx = #{idx}
