mapper.update:
UPDATE vacation
SET code = #{code}, start = #{start}, end = #{end}, content = #{content}
WHERE idx = #{idx}

mapper.select:
SELECT GROUP_CONCAT(vh.date ORDER BY vh.date SEPARATOR ',') AS date
FROM vacation v JOIN vacation_detail_date vh ON v.idx = vh.vacation_idx
WHERE vacation_idx = #{vacationIdx}
GROUP BY v.idx, v.content, v.start, v.end, vh.vacation_idx
ORDER BY v.idx;
